<html>   <head>       <link rel="StyleSheet" href="estilos.php" type="text/css">  <title>Agregar Cartera</title>   </head>   <body>    <script>  window.addEventListener('load', inicio, false);  function inicio() {    document.getElementById("Form_agregar_cartera").addEventListener('submit', validar, false);  }  function validar(evt) {   var nombre=document.getElementById('cartera').value;   var n = nombre.indexOf(" ");   if(n>0){	var res = nombre.replace(" ", "-");   	document.getElementById('cartera').value=res;    alert('Los nombres de cartera no deben tener espacios, por tanto estos se reemplazar con el signo "-"');   }  }</script>  <br> <br> <br>  <h2> Agregar cartera: </h2>  <form method="post" action="Agregar_cartera.php" id="Form_agregar_cartera">   Nombre:  <input type="text" name="nombre" id="cartera" required>   <br> <br>    <input type="submit" value="Ingresar">   </form>  <br> <br>  <a href="Lobby.php">Ir a Lobby</a></body> </html>