 excedente= parseInt(sumatorio)*inte;	 var total_excedente=excedente*parseInt(meses);	 var total_credito=total_excedente+parseInt(sumatorio);	 var n_cuotas=parseInt(document.getElementById('input_cuotas').value);	 var cuotas=total_credito/n_cuotas;	 document.getElementById('input_valor_cuotas').value=cuotas;	 document.getElementById('input_total_pagar').value=total_credito;	    }     function cargar_credito() {     check_contado=document.getElementById('check_contado');	 check_contado.checked = false;	 check_credito=document.getElementById('check_credito');	 check_credito.checked = true;	 var sumatorio="<?php echo $sumatorio; ?>";     document.getElementById('prestamo').value=sumatorio;	 var meses = document.getElementById('input_meses').value;	 var inte= parseInt(document.getElementById('input_int').value)/100;	 var excedente= parseInt(sumatorio)*inte;	 var total_excedente=excedente*parseInt(meses);	 var total_credito=total_excedente+parseInt(sumatorio);	 var n_cuotas=parseInt(document.getElementById('input_cuotas').value);	 var cuotas=total_credito/n_cuotas;	 document.getElementById('input_valor_cuotas').value=cuotas;	 document.getElementById('input_total_pagar').value=total_credito;  }  function validar(evt) {     var nom=document.getElementById('prestamo').value;     var patron=/^[0-9]+$/;    if (patron.test(nom)) {		} else {    alert('El campo valor prestamo solo puede guardar numeros!');		evt.preventDefault();		}  }   function fecha_prest() {	var fecha = new Date(); //Fecha actual	var mes = fecha.getMonth()+1; //obteniendo mes	var dia = fecha.getDate(); //obteniendo dia	var ano = fecha.getFullYear(); //obteniendo año	if(dia<10)    dia='0'+dia; //agrega cero si el menor de 10	if(mes<10)    mes='0'+mes //agrega cero si el menor de 10	document.getElementById('date').value=ano+"-"+mes+"-"+dia;  }</script></html>