tamos WHERE CEDULA='{$cedula}'";										$resultado2=mysqli_query($conexion,$consulta2);										if($reg2=$resultado2->fetch_array()){											$get_total_saldo=$reg2['totalapagar'];										}											$total_saldo=$get_total_saldo-$total_abonado;									   $insert_abono="INSERT INTO abonos(cedula, fecha, altura_cuota, valor_abono, asesor, fingreso, Cobro) VALUES ($cedula, $fecha, $altura_cuota, $valor_abono, $asesor, $fingreso, $Cobro)";									$actualizar_total="update prestamos set tt_abonos='$total_abonado', tt_saldo='$total_saldo' where cedula='$cedula'";									$resultado_insert=mysqli_query($conexion,$insert_abono) or die ("problemas al insertar");									$resultado_actualizar=mysqli_query($conexion,$actualizar_total);									if($resultado_insert){										echo 'Registra';									}																	}						}														               // fin corchetes					 				 }  else {     echo 'No registra'; }?>